const request_response = $input.first().json;
const category_id = $('Update Category').first().json.category_id;

let locations = [];
let message = '';

if (category_id === 1) {
  // mostrar locaciones de zero_or_one
  locations = Object.keys(request_response.access_with_zero_or_one_scan_peripheral);
  message = "¿En qué locación presentas el problema?\n";
} else if (category_id === 2) {
  // mostrar locaciones de multiple
  locations = Object.keys(request_response.access_with_multiple_scan_peripherals);
  message = "¿En qué locación presentas el problema?\n";
} else if (category_id === 4) {
  // cámaras
  locations = Object.keys(request_response.cameras);
  message = "¿En qué cámara presentas el problema?\n";
} else {
  message = "No hay opciones disponibles para esta categoría.";
  return [{ json: { message } }];
}

// construir enumeración con emojis numéricos
locations.forEach((loc, index) => {
  const num = index + 1;
  let numberEmoji = '';

  if (num <= 9) {
    numberEmoji = `${num}️⃣`; // 1️⃣ - 9️⃣
  } else {
    // convierte 10, 11, etc. a “1️⃣0️⃣”, “1️⃣1️⃣”, etc.
    numberEmoji = num
      .toString()
      .split('')
      .map(digit => `${digit}️⃣`)
      .join('');
  }

  message += `${numberEmoji} ${loc}\n`;
});


return [{ json: { message } }];

// obtiene el array del input
const issues = $input.all().map(item => item.json);

// construye el mensaje enumerado con ids autoincrementales
let message = "*¿Qué problema se asocia a lo que estás pasando?*\n";
message += "Por favor elige una opción:\n\n";

// enumerar del 1 en adelante
issues.forEach((issue, index) => {
  const number = index + 1;
  message += `${number}️⃣ *${issue.name}*\n`;
});

// salida
return [
  {
    json: {
      whatsapp_message: message
    }
  }
];

const usseles = $('Evaluate existing or new chat').first().json.body.data.message.conversation;
let whatsapp_message;
let end_process;
let usseles_solution;
if (usseles == 1) {
  whatsapp_message = "✅ Gracias por confiar en nuestro sistema, espero haber ayudado.";
  usseles_solution=true;

   end_process= true;
} else if (usseles == 2) {
  whatsapp_message = "📨 Vamos a escalar tu caso a un asesor para brindarte una atención más detallada. Por favor, cuéntanos exactamente lo que ocurrió con todos los detalles posibles, y enviaremos la información al equipo correspondiente.";
  usseles_solution=false;
  end_process= true;
} else {
  whatsapp_message = "⚠️ La opción ingresada no es válida, por favor intente de nuevo.";
  end_process= false;
  usseles_solution= false;
}

return [
  {
    json: {
      end_process,

      whatsapp_message,
      usseles_solution
    }
  }
];

