const items = $input.all();

let message = "¿Qué problema presenta?\n";

items.forEach(item => {
  const num = item.json.id;
  const emojiNum = num <= 10 ? `${num}️⃣` : `${num}.`; // hasta 10 usa emoji, más de 10 con punto
  message += `${emojiNum} ${item.json.description}\n`;
});

return [{ json: { message } }];

// Entradas
const selectedRaw = $('Webhook').first().json.body.data.message.conversation;
const location = $('Get Active chat').first().json.location_name;
let device = $('Get Active chat').first().json.device;

const company_info = $input.first().json.propertyName;

// Validar que selected sea numérico
const selected = parseInt(selectedRaw) -1;
if (isNaN(selected)) {
  return [{ json: { error: "La opción ingresada no es un número válido." } }];
}

// Si la location está vacía, continuar
if (!location || location.trim() === '') {
  // obtener listado de locaciones
  const locations = Object.keys(company_info);

  // validar índice dentro del rango
  if (selected < 0 || selected >= locations.length) {
    return [{ json: { error: "Índice fuera de rango." } }];
  }

  // obtener la locación según índice
  const selectedLocation = locations[selected];

  // obtener dispositivos de esa locación
  const devicesStr = company_info[selectedLocation];
  const devices = devicesStr.split(',');

  // si solo hay un dispositivo, asignarlo
  device = devices[0];

  return [{
    json: {
      location: selectedLocation,
      device: device
    }
  }];
} else {
  // si location ya viene lleno, no hacer nada
  return [{
    json: {
      location: location,
      device: device
    }
  }];
}
